%dw 2.0
output application/xml
ns soap http://schemas.xmlsoap.org/soap/envelope
---
{soap#Envelope:{
    soap#Body: {
        soap#Fault: {
            faultcode: "soap:Server",
            faultstring: error.description,
            detail: error.errorType.namespace as String ++ ":" ++ error.errorType.identifier as String
        }
    }
    } 
}

